clock t;

void updatePosition(int target)
{
    if(target == MOVEMENT0_P0)
    {
        positions[id] = 0;
    }
    else if(target == MOVEMENT0_P1)
    {
        positions[id] = 1;
    }
    else if(target == MOVEMENT0_P2)
    {
        positions[id] = 2;
    }
    else if(target == MOVEMENT0_P3)
    {
        positions[id] = 3;
    }
    else
    {
        positions[id] = 4;
    }
}

void update(int target)
{
    t = 0;
    idle[id] = false;
    scheduled[id] = true;
    locations[id][MOVEMENT]=target;
    updatePosition(target);
}

void react(int target)
{
    t = 0;
    idle[id] = true;
    scheduled[id] = false;
    locations[id][MOVEMENT]=target;
    updatePosition(target);
}

clock t;
const int[0,TaskNum] rules[MilestoneNum] = {0,1,2,3};

void updateIteration()
{
	int i=0;
	bool finish = true;
	for(i=1;i<=RegularTaskNum[id];i++)
	{
		finish &= tf[id][i];
	}
	if(finish)
	{
		for(i=1;i<=RegularTaskNum[id];i++)
		{
			tf[id][i]=false;
		}

		iteration[id]+=1;
		if(iteration[id]>=MaxIteration)
		{
            scheduled[id] = true;
			iteration[id]=MaxIteration;
            print();
		}
	}
	else
	{
    	finish = true;
	}
}

int getTaskID(int taskID)
{
    return --taskID;
}

bool isNecessary(int taskID)
{
    return !tf[id][taskID]&&iteration[id]<MaxIteration;
}

bool isBusy(int taskID)
{
    int id = 0;
    bool busy = false;

    for(id = 0; id < AgentNum; id++)
    {
        if(ts[id][taskID])
        {
            busy = true;
            return busy;
        }
    }

    return busy;
}

bool shouldWait()
{
    int currentPosition, currentTask;
    bool currentFinished, ready, wait, busy;

    currentPosition = positions[id];
    if(currentPosition > 0 && currentPosition < MilestoneNum)
    {
        currentTask = rules[currentPosition];
        currentFinished = tf[id][currentTask];
        ready = idle[id]&&iteration[id]<MaxIteration;
        busy = isBusy(currentTask);
        wait = !currentFinished && ready && busy;
    }
    else
    {
        wait = false;
    }

    return wait;   
}

bool isConducted(int target)
{
    int taskID = getTaskID(target);
    bool isNext = (next_action.agent_id == id) &&
                   (next_action.action_type == TASKEXE) &&
                   (next_action.target_location_id == target);
    bool isAllowed = false;

    if(taskID >= 0)
    {
        isAllowed = no_action && !isBusy(taskID) && isNecessary(taskID);
    }
    else
    {
        isAllowed = no_action;
    }

    return idle[id] && (isAllowed || isNext);
}

void update(int target)
{
    t = 0;
    idle[id] = false;
    scheduled[id] = true;
    locations[id][TASKEXE]=target;
}

void react(int target)
{
    t = 0;
    idle[id] = true;
    scheduled[id] = false;
    locations[id][TASKEXE]=target;
    updateIteration();
}

bool minimization = false;
void makeDecision()
{
    int l_tf0[TaskNum], l_tf1[TaskNum], l_tf2[TaskNum];
    int l_location0[2], l_location1[2], l_location2[2];
    int l_iteration[AgentNum];
    int i = 0, j = 0, action_id = -1, l_task_number = TaskNum;

    for(i = 0; i < TaskNum; i++)
    {
        l_tf0[i] = tf[0][i];
        l_tf1[i] = tf[1][i];
        l_tf2[i] = tf[2][i];
    }

    l_location0[MOVEMENT] = locations[0][MOVEMENT];
    l_location0[TASKEXE] = locations[0][TASKEXE];
    l_location1[MOVEMENT] = locations[1][MOVEMENT];
    l_location1[TASKEXE] = locations[1][TASKEXE];
    l_location2[MOVEMENT] = locations[2][MOVEMENT];
    l_location2[TASKEXE] = locations[2][TASKEXE];

    for(i = 0; i < AgentNum; i++)
    {
        l_iteration[i] = iteration[i];
    }

    action_id = make_decision(minimization, l_iteration, l_task_number, l_tf0, l_tf1, l_tf2, l_location0, l_location1, l_location2);

    if(action_id != -1)
    {
        next_action = map_actions[action_id];
        no_action = false;
    }
    else
    {
        no_action = true;
    }
}

bool IsMove()
{
    return no_action || (!no_action && next_action.action_type == MOVEMENT);
}

bool IsExe()
{
    return no_action || (!no_action && next_action.action_type == TASKEXE);
}

bool isFinish()
{
    int i = 0;
    bool finish = true; 

    for(i = 0; i < AgentNum; i++)
    {
        finish &= (iteration[i] >= MaxIteration);
    }

    return finish;
}

bool isScheduled()
{
    int i = 0;
    bool ready = true; 

    for(i = 0; i < AgentNum; i++)
    {
        ready &= (scheduled[i] || iteration[i]>=MaxIteration);
    }

    return ready;
}

void updateAction()
{
    next_action.agent_id = -1;
    next_action.action_type = -1;
    next_action.original_location_id = -1;
    next_action.target_location_id = -1;
}

